sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {	
	},

	isDatePast: function(strDate){

		// Get the date portion of rightnow and testdate (no timestamp)  use *.getLocalDate() toget the date part
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	//u_when_needed date cannot be today. compare *the date parts of* rightnow var to passed in variable
	isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		var today = rightnow.getLocalDate();
		var passedinvar = testdate.getLocalDate();

		if (today.compareTo(passedinvar) == 0){
			return true;
		}
		else {
			return false;
		}

	},

	type: 'NeedItUtils'
};
